mapper.update:
update ordersinfo
set orderinfoid = #{orderinfoid,jdbcType=BIGINT},
  isbn = #{isbn,jdbcType=BIGINT},
  `number` = #{number,jdbcType=INTEGER}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, orderinfoid, isbn, `number`
from ordersinfo
where id = #{id,jdbcType=BIGINT}
